import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import axios from "axios";

const BASE_NODE_URL = process.env.REACT_APP_BASE_NODE_URL || "http://localhost:5000";

function App() {
  const [username, setUsername] = useState("");
  const [recipient, setRecipient] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [connected, setConnected] = useState(false);
  const [relayUrl, setRelayUrl] = useState("");
  const [status, setStatus] = useState("Connecting");
  const [error, setError] = useState("");
  const socketRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (!username) return;
    setStatus("Connecting");
    setError("");
    // Get relay server from base node
    axios.get(`${BASE_NODE_URL.replace(/\/$/, "")}/relay`)
      .then(res => {
        if (res.data.success && res.data.relay) {
          const relay = res.data.relay;
          const url = `http://${relay.ip}:${relay.port}`;
          setRelayUrl(url);
          connectToRelay(url);
        } else {
          setError("No relay server available. Try again later.");
          setStatus("Disconnected");
        }
      })
      .catch(() => {
        setError("Failed to connect to base node.");
        setStatus("Disconnected");
      });
    // eslint-disable-next-line
  }, [username]);

  const connectToRelay = (url) => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }
    const socket = io(url);
    socketRef.current = socket;
    socket.on("connect", () => {
      setConnected(true);
      setStatus("Connected");
      socket.emit("register", { username }, (response) => {
        if (!response.success) {
          setError(response.reason || "Registration failed");
          setStatus("Error");
        }
      });
    });
    socket.on("disconnect", () => {
      setConnected(false);
      setStatus("Disconnected");
    });
    socket.on("receiveMessage", ({ from, message }) => {
      setMessages((prev) => [...prev, { from, message }]);
    });
    socket.on("connect_error", () => {
      setError("Failed to connect to relay server.");
      setStatus("Error");
    });
  };

  const handleSend = (e) => {
    e.preventDefault();
    if (!recipient || !message) return;
    if (!socketRef.current) return;
    socketRef.current.emit(
      "sendMessage",
      { to: recipient, message },
      (response) => {
        if (response && response.delivered) {
          setMessages((prev) => [...prev, { from: username, message }]);
          setMessage("");
        } else {
          setError(response?.reason || "Message delivery failed");
        }
      }
    );
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (!username) return;
    setMessages([]);
    setError("");
    setRecipient("");
  };

  return (
    <div style={{ minHeight: "100vh", background: "#181c20", color: "#fff", display: "flex", alignItems: "center", justifyContent: "center" }}>
      <div style={{ background: "#23272f", padding: 32, borderRadius: 16, minWidth: 350, boxShadow: "0 4px 32px #0006" }}>
        <h1 style={{ textAlign: "center", fontSize: 32, marginBottom: 16 }}>WhisperNet Messenger</h1>
        <div style={{ textAlign: "center", marginBottom: 12 }}>
          <span style={{ fontWeight: 600 }}>Status:</span> <span style={{ color: status === "Connected" ? "#4ecdc4" : status === "Error" ? "#ff6b6b" : "#ffe66d" }}>{status}</span>
        </div>
        {error && <div style={{ color: "#ff6b6b", marginBottom: 12, textAlign: "center" }}>{error}</div>}
        {!connected ? (
          <form onSubmit={handleLogin} style={{ display: "flex", flexDirection: "column", gap: 12 }}>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              style={{ padding: 10, borderRadius: 6, border: "none", fontSize: 16 }}
              autoFocus
            />
            <button type="submit" style={{ padding: 10, borderRadius: 6, background: "#4ecdc4", color: "#181c20", fontWeight: 700, fontSize: 16, border: "none", cursor: "pointer" }}>Login</button>
          </form>
        ) : (
          <>
            <div style={{ marginBottom: 12 }}>
              <input
                type="text"
                placeholder="Recipient username"
                value={recipient}
                onChange={e => setRecipient(e.target.value)}
                style={{ padding: 8, borderRadius: 6, border: "none", fontSize: 15, width: "100%" }}
              />
            </div>
            <div style={{ background: "#181c20", borderRadius: 8, minHeight: 180, maxHeight: 220, overflowY: "auto", marginBottom: 12, padding: 10 }}>
              {messages.map((msg, idx) => (
                <div key={idx} style={{ marginBottom: 8, textAlign: msg.from === username ? "right" : "left" }}>
                  <span style={{ fontWeight: 600, color: msg.from === username ? "#4ecdc4" : "#ffe66d" }}>{msg.from}:</span> <span>{msg.message}</span>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
            <form onSubmit={handleSend} style={{ display: "flex", gap: 8 }}>
              <input
                type="text"
                placeholder="Type your message..."
                value={message}
                onChange={e => setMessage(e.target.value)}
                style={{ flex: 1, padding: 8, borderRadius: 6, border: "none", fontSize: 15 }}
              />
              <button type="submit" style={{ padding: 8, borderRadius: 6, background: "#4ecdc4", color: "#181c20", fontWeight: 700, fontSize: 15, border: "none", cursor: "pointer" }}>Send</button>
            </form>
          </>
        )}
        <div style={{ marginTop: 18, fontSize: 13, color: "#aaa", textAlign: "center" }}>
          Relay: {relayUrl ? <span style={{ color: "#ffe66d" }}>{relayUrl}</span> : "-"}
        </div>
        <div style={{ marginTop: 4, fontSize: 12, color: "#444", textAlign: "center" }}>
          Base Node: <span style={{ color: "#ffe66d" }}>{BASE_NODE_URL}</span>
        </div>
      </div>
    </div>
  );
}

export default App;
