import React, { useState, useEffect, useRef } from 'react';

// ChatManager component handles the UI for chat tabs, user list, and message history
const ChatManager = ({ 
  username, 
  messages, 
  onSendMessage, 
  onlineUsers, 
  recipient, 
  setRecipient, 
  message, 
  setMessage, 
  recipientStatus, 
  checkRecipientStatus,
  typing,
  handleMessageChange,
  handleRelayBounce,
  formatMessageTime,
  getTimestamp
}) => {
  // State for managing chat tabs and history
  const [chatTabs, setChatTabs] = useState([]);
  const [activeTab, setActiveTab] = useState(null);
  const [chatHistory, setChatHistory] = useState({});
  const [unreadCounts, setUnreadCounts] = useState({});
  const messagesEndRef = useRef(null);

  // Initialize chat history from localStorage on component mount
  useEffect(() => {
    if (username) {
      const storedHistory = localStorage.getItem(`whispernetChatHistory_${username}`);
      if (storedHistory) {
        try {
          const parsedHistory = JSON.parse(storedHistory);
          setChatHistory(parsedHistory);
          
          // Initialize chat tabs from history
          const tabs = Object.keys(parsedHistory);
          setChatTabs(tabs);
          
          // Set active tab if recipient is already set
          if (recipient && tabs.includes(recipient)) {
            setActiveTab(recipient);
          }
        } catch (error) {
          console.error('Error parsing chat history:', error);
          // If there's an error, start with empty history
          setChatHistory({});
        }
      }
    }
  }, [username, recipient]);

  // Update chat history when new messages arrive
  useEffect(() => {
    if (username && messages.length > 0) {
      const newHistory = { ...chatHistory };
      
      // Group messages by sender/recipient
      messages.forEach(msg => {
        const chatPartner = msg.from === username ? msg.to || recipient : msg.from;
        if (chatPartner) {
          if (!newHistory[chatPartner]) {
            newHistory[chatPartner] = [];
          }
          
          // Check if message already exists in history to avoid duplicates
          const messageExists = newHistory[chatPartner].some(
            existingMsg => 
              existingMsg.timestamp && msg.timestamp && 
              new Date(existingMsg.timestamp).getTime() === new Date(msg.timestamp).getTime() && 
              existingMsg.message === msg.message
          );
          
          if (!messageExists) {
            newHistory[chatPartner].push(msg);
          }
        }
      });
      
      setChatHistory(newHistory);
      
      // Save to localStorage
      localStorage.setItem(`whispernetChatHistory_${username}`, JSON.stringify(newHistory));
      
      // Update chat tabs
      const updatedTabs = Object.keys(newHistory);
      setChatTabs(updatedTabs);
      
      // Update unread counts
      const newUnreadCounts = { ...unreadCounts };
      messages.forEach(msg => {
        if (msg.from !== username && msg.from !== activeTab) {
          newUnreadCounts[msg.from] = (newUnreadCounts[msg.from] || 0) + 1;
        }
      });
      setUnreadCounts(newUnreadCounts);
    }
  }, [messages, username, chatHistory, recipient, activeTab, unreadCounts]);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, activeTab, chatHistory]);

  // Handle tab click
  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
    setRecipient(tabName);
    checkRecipientStatus();
    
    // Clear unread count for this tab
    if (unreadCounts[tabName]) {
      const newUnreadCounts = { ...unreadCounts };
      delete newUnreadCounts[tabName];
      setUnreadCounts(newUnreadCounts);
    }
  };

  // Handle new chat
  const startNewChat = () => {
    setActiveTab(null);
    setRecipient('');
  };

  // Handle clear chat history
  const clearChatHistory = (chatPartner = null) => {
    if (chatPartner) {
      // Clear specific chat
      const newHistory = { ...chatHistory };
      delete newHistory[chatPartner];
      setChatHistory(newHistory);
      localStorage.setItem(`whispernetChatHistory_${username}`, JSON.stringify(newHistory));
      
      // Update tabs
      setChatTabs(Object.keys(newHistory));
      
      // If we cleared the active tab, reset it
      if (activeTab === chatPartner) {
        setActiveTab(null);
        setRecipient('');
      }
    } else {
      // Clear all chat history
      setChatHistory({});
      localStorage.removeItem(`whispernetChatHistory_${username}`);
      setChatTabs([]);
      setActiveTab(null);
      setRecipient('');
    }
  };

  // Get messages for active tab
  const getActiveTabMessages = () => {
    if (activeTab && chatHistory[activeTab]) {
      return chatHistory[activeTab];
    }
    return [];
  };

  // Handle send message
  const handleSend = (e) => {
    e.preventDefault();
    if (!recipient || !message.trim()) return;
    
    // Call the parent send function
    onSendMessage(e, false);
    
    // Add the message to chat history
    const newMessage = {
      from: username,
      to: recipient,
      message: message,
      timestamp: new Date(),
    };
    
    const newHistory = { ...chatHistory };
    if (!newHistory[recipient]) {
      newHistory[recipient] = [];
    }
    newHistory[recipient].push(newMessage);
    setChatHistory(newHistory);
    
    // Save to localStorage
    localStorage.setItem(`whispernetChatHistory_${username}`, JSON.stringify(newHistory));
    
    // Update chat tabs
    const updatedTabs = Object.keys(newHistory);
    setChatTabs(updatedTabs);
    
    // Set active tab to recipient
    setActiveTab(recipient);
  };

  return (
    <div style={{ display: 'flex', height: '100%' }}>
      {/* Left sidebar with user list and chat tabs */}
      <div style={{ 
        width: '250px', 
        borderRight: '1px solid #1e2d3d', 
        display: 'flex', 
        flexDirection: 'column',
        background: '#0d1117'
      }}>
        {/* New Chat button */}
        <div style={{ padding: '12px' }}>
          <button 
            style={{ 
              width: '100%', 
              padding: '8px', 
              background: '#5ccfe6', 
              color: '#171c28', 
              border: 'none', 
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold',
              fontFamily: '"Fira Code", monospace'
            }}
            onClick={startNewChat}
          >
            + New Chat
          </button>
        </div>
        
        {/* Chat tabs */}
        <div style={{ 
          flex: '1', 
          overflowY: 'auto',
          borderBottom: '1px solid #1e2d3d',
          padding: '0 8px'
        }}>
          <h3 style={{ 
            color: '#5ccfe6', 
            fontSize: '14px', 
            padding: '8px 4px',
            margin: '0'
          }}>
            Recent Chats
          </h3>
          
          {chatTabs.length === 0 ? (
            <div style={{ 
              color: '#636b78', 
              fontSize: '12px', 
              padding: '8px 4px' 
            }}>
              No recent chats
            </div>
          ) : (
            chatTabs.map(tab => (
              <div 
                key={tab} 
                style={{ 
                  padding: '8px 4px', 
                  margin: '4px 0',
                  background: activeTab === tab ? 'rgba(92, 207, 230, 0.1)' : 'transparent',
                  borderLeft: activeTab === tab ? '2px solid #5ccfe6' : '2px solid transparent',
                  cursor: 'pointer',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  borderRadius: '2px'
                }}
                onClick={() => handleTabClick(tab)}
              >
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <div style={{ 
                    width: '8px', 
                    height: '8px', 
                    borderRadius: '50%', 
                    background: onlineUsers.includes(tab) ? '#bae67e' : '#ff8f40',
                    marginRight: '8px' 
                  }}></div>
                  <span style={{ 
                    fontSize: '14px',
                    fontWeight: activeTab === tab ? 'bold' : 'normal'
                  }}>
                    {tab}
                  </span>
                </div>
                
                {/* Unread count badge */}
                {unreadCounts[tab] && (
                  <span style={{ 
                    background: '#ff3333', 
                    color: 'white', 
                    borderRadius: '50%', 
                    padding: '2px 6px', 
                    fontSize: '10px',
                    minWidth: '10px',
                    textAlign: 'center'
                  }}>
                    {unreadCounts[tab]}
                  </span>
                )}
                
                {/* Clear chat button */}
                <button 
                  style={{ 
                    background: 'none', 
                    border: 'none', 
                    color: '#636b78', 
                    cursor: 'pointer',
                    fontSize: '14px',
                    padding: '2px',
                    marginLeft: '4px',
                    display: activeTab === tab ? 'block' : 'none'
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    if (window.confirm(`Clear chat history with ${tab}?`)) {
                      clearChatHistory(tab);
                    }
                  }}
                  title="Clear chat history"
                >
                  ×
                </button>
              </div>
            ))
          )}
        </div>
        
        {/* Online users */}
        <div style={{ 
          flex: '1', 
          overflowY: 'auto',
          padding: '0 8px'
        }}>
          <h3 style={{ 
            color: '#bae67e', 
            fontSize: '14px', 
            padding: '8px 4px',
            margin: '0',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <span>Online Users</span>
            <span style={{ 
              background: '#1c4b3c', 
              color: '#bae67e', 
              borderRadius: '4px', 
              padding: '2px 6px', 
              fontSize: '10px' 
            }}>
              {onlineUsers.length}
            </span>
          </h3>
          
          {onlineUsers.length === 0 ? (
            <div style={{ 
              color: '#636b78', 
              fontSize: '12px', 
              padding: '8px 4px' 
            }}>
              No users online
            </div>
          ) : (
            onlineUsers.map(user => (
              user !== username && (
                <div 
                  key={user} 
                  style={{ 
                    padding: '8px 4px', 
                    margin: '4px 0',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    borderRadius: '2px'
                  }}
                  onClick={() => {
                    setRecipient(user);
                    setActiveTab(user);
                    checkRecipientStatus();
                  }}
                >
                  <div style={{ 
                    width: '8px', 
                    height: '8px', 
                    borderRadius: '50%', 
                    background: '#bae67e',
                    marginRight: '8px' 
                  }}></div>
                  <span style={{ fontSize: '14px' }}>
                    {user}
                  </span>
                </div>
              )
            ))
          )}
        </div>
        
        {/* Clear all history button */}
        <div style={{ padding: '12px' }}>
          <button 
            style={{ 
              width: '100%', 
              padding: '8px', 
              background: '#4b1c1c', 
              color: '#ff8f40', 
              border: 'none', 
              borderRadius: '4px',
              cursor: 'pointer',
              fontFamily: '"Fira Code", monospace',
              fontSize: '12px'
            }}
            onClick={() => {
              if (window.confirm('Clear all chat history? This cannot be undone.')) {
                clearChatHistory();
              }
            }}
          >
            Clear All History
          </button>
        </div>
      </div>
      
      {/* Main chat area */}
      <div style={{ 
        flex: '1', 
        display: 'flex', 
        flexDirection: 'column',
        overflow: 'hidden',
        padding: '0 24px'
      }}>
        {/* Message input form */}
        <form onSubmit={handleSend} style={{ 
          display: 'flex', 
          flexDirection: 'column', 
          padding: '16px 0'
        }}>
          <div style={{ display: 'flex', marginBottom: 8 }}>
            <input
              style={{ 
                flex: 1, 
                padding: 10, 
                borderRadius: 4, 
                border: '1px solid #1e2d3d', 
                background: '#0d1117',
                color: '#a2aabc',
                fontSize: 14,
                marginRight: 8,
                fontFamily: '"Fira Code", monospace'
              }}
              placeholder="Recipient username"
              value={recipient}
              onChange={(e) => {
                setRecipient(e.target.value);
                setActiveTab(null);
              }}
              required
            />
            {recipient && (
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                marginLeft: 8,
                fontSize: 12
              }}>
                <div style={{ 
                  width: 8, 
                  height: 8, 
                  borderRadius: '50%', 
                  background: recipientStatus.online ? '#bae67e' : 
                             recipientStatus.exists ? '#ff8f40' : '#ff3333',
                  marginRight: 6 
                }}></div>
                <span style={{
                  fontSize: 12,
                  color: recipientStatus.online ? '#bae67e' : 
                        recipientStatus.exists ? '#ff8f40' : '#ff3333'
                }}>
                  {recipientStatus.online ? 'ONLINE' : 'OFFLINE'}
                </span>
              </div>
            )}
          </div>
          <div style={{ display: 'flex' }}>
            <input
              style={{ 
                flex: 1, 
                padding: 10, 
                borderRadius: 4, 
                border: '1px solid #1e2d3d', 
                background: '#0d1117',
                color: '#a2aabc',
                fontSize: 14,
                marginRight: 8,
                fontFamily: '"Fira Code", monospace'
              }}
              placeholder="Type a message"
              value={message}
              onChange={handleMessageChange}
              required
            />
            {recipient ? (
              <div style={{ display: 'flex' }}>
                {/* SEND button */}
                <button 
                  style={{ 
                    padding: '0 18px', 
                    borderRadius: '4px 0 0 4px', 
                    background: recipientStatus.online ? 
                      'linear-gradient(90deg, #5ccfe6, #bae67e)' : 
                      '#636b78', 
                    color: '#171c28', 
                    fontWeight: 'bold', 
                    fontSize: 14, 
                    border: 'none',
                    cursor: recipientStatus.online ? 'pointer' : 'not-allowed',
                    fontFamily: '"Fira Code", monospace'
                  }} 
                  type="submit"
                  disabled={!recipientStatus.online}
                  title={recipientStatus.online ? "Send message directly" : "User is offline or not found"}
                >
                  SEND
                </button>
                
                {/* RELAY button */}
                <button 
                  style={{ 
                    padding: '0 12px', 
                    borderRadius: '0 4px 4px 0', 
                    background: '#4b1c1c', 
                    color: '#ff8f40', 
                    fontWeight: 'bold', 
                    fontSize: 14, 
                    border: 'none',
                    cursor: 'pointer',
                    fontFamily: '"Fira Code", monospace',
                    display: 'flex',
                    alignItems: 'center'
                  }} 
                  onClick={handleRelayBounce}
                  title="Message will be stored on relay servers for up to 4 hours"
                >
                  RELAY
                  <span style={{
                    fontSize: 10,
                    marginLeft: 4,
                    background: 'rgba(255, 143, 64, 0.2)',
                    padding: '1px 3px',
                    borderRadius: 2
                  }}>
                    4h
                  </span>
                </button>
              </div>
            ) : (
              <button 
                style={{ 
                  padding: '0 18px', 
                  borderRadius: 4, 
                  background: '#636b78', 
                  color: '#171c28', 
                  fontWeight: 'bold', 
                  fontSize: 14, 
                  border: 'none',
                  cursor: 'not-allowed',
                  fontFamily: '"Fira Code", monospace'
                }} 
                disabled={true}
              >
                SEND
              </button>
            )}
          </div>
        </form>
        
        {typing && recipient && (
          <div style={{ fontSize: 12, color: '#5ccfe6', marginBottom: 8 }}>
            {recipient} is typing...
          </div>
        )}
        
        {/* Messages display */}
        <div style={{ 
          background: '#0d1117', 
          borderRadius: 4, 
          padding: 12, 
          flex: 1,
          overflowY: 'auto', 
          marginBottom: 12,
          border: '1px solid #1e2d3d',
          fontFamily: 'monospace'
        }}>
          {(!activeTab || getActiveTabMessages().length === 0) ? (
            <div style={{ color: '#5ccfe6', fontSize: 14 }}>
              {getTimestamp()} Connection established. Awaiting transmission...
            </div>
          ) : (
            getActiveTabMessages().map((msg, i) => (
              <div key={i} style={{ 
                marginBottom: 12, 
                padding: 8,
                borderRadius: 4,
                background: msg.from === username ? 'rgba(92, 207, 230, 0.1)' : 'rgba(186, 230, 126, 0.1)',
                borderLeft: msg.from === username ? '2px solid #5ccfe6' : '2px solid #bae67e'
              }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  marginBottom: 4, 
                  fontSize: 12 
                }}>
                  <span style={{ 
                    color: msg.from === username ? '#5ccfe6' : '#bae67e',
                    fontWeight: 'bold'
                  }}>
                    {msg.from === username ? 'You' : msg.from}
                  </span>
                  <span style={{ color: '#636b78' }}>
                    {formatMessageTime(msg.timestamp)}
                  </span>
                </div>
                <div style={{ wordBreak: 'break-word' }}>
                  {msg.message}
                </div>
                {msg.encrypted && (
                  <div style={{ 
                    fontSize: 10, 
                    color: '#5ccfe6', 
                    marginTop: 4,
                    display: 'flex',
                    alignItems: 'center'
                  }}>
                    <span style={{ 
                      display: 'inline-block',
                      width: 8,
                      height: 8,
                      borderRadius: '50%',
                      background: msg.decryptionStatus === 'decrypted' ? '#bae67e' : '#ff8f40',
                      marginRight: 4
                    }}></span>
                    {msg.decryptionStatus === 'decrypted' ? 'Encrypted' : 'Encryption Failed'}
                  </div>
                )}
                {msg.bounced && (
                  <div style={{ 
                    fontSize: 10, 
                    color: '#ff8f40', 
                    marginTop: 4,
                    display: 'flex',
                    alignItems: 'center'
                  }}>
                    <span style={{ marginRight: 4 }}>⟳</span>
                    Relayed Message
                  </div>
                )}
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>
    </div>
  );
};

export default ChatManager;
